mapper.select:
SELECT CD_NM, CD
FROM CODE_INFO
WHERE CD_CLSF =0002;

SELECT * FROM crclmListView
WHERE 1=1

	AND KORN_FLNM LIKE CONCAT('%',#{insname},'%');		


	AND EDU_CN LIKE CONCAT('%',#{crclmname},'%');		


	AND CRCLM_YEAR = #{year}


	AND CRCLM_HALF = #{half}


	AND CRCLM_SCHDL_CD =#{radioval}
