mapper.select:
SELECT *  
FROM crclmListView


		AND CRCLM_NM LIKE CONCAT('%',#{crclmName},'%')		


		AND CRCLM_YEAR = #{year}


		AND CRCLM_HALF = #{half}


		AND CRCLM_SCHDL_CD =#{radioVal}                                                                                                                                          


ORDER BY CRCLM_NO ASC;

SELECT CD_NM, CD
FROM CODE_INFO
WHERE CD_CLSF =0002;

SELECT SCHDL_BGNG_DT, SCHDL_END_DT, CodeFind('0003',CRCLM_SCHDL.CRCLM_SCHDL_CD) AS CRCLM_SCHDL_CD, SCHDL_EXPLN, SCHDL_FIN
FROM CRCLM_SCHDL
WHERE CRCLM_YEAR = #{cyear} AND CRCLM_HALF =#{chalf} AND CRCLM_CD =#{ccd}

mapper.update:
UPDATE CRCLM_SCHDL SET SCHDL_BGNG_DT = CASE
WHEN CRCLM_SCHDL_CD = CodeFindRev('0003', #{item.schdlCode}) THEN #{item.schdlBegin}
ELSE SCHDL_BGNG_DT END, SCHDL_END_DT = CASE
WHEN CRCLM_SCHDL_CD = CodeFindRev('0003', #{item.schdlCode}) THEN #{item.schdlEnd}
ELSE SCHDL_END_DT END, SCHDL_EXPLN = CASE
WHEN CRCLM_SCHDL_CD = CodeFindRev('0003', #{item.schdlCode}) THEN #{item.schdlEx}
ELSE SCHDL_EXPLN END, SCHDL_FIN = CASE
WHEN CRCLM_SCHDL_CD = CodeFindRev('0003', #{item.schdlCode}) THEN #{item.schdlFin}
ELSE SCHDL_FIN END
(CRCLM_CD = #{item.ccd} AND CRCLM_YEAR = #{item.cyear} AND CRCLM_HALF = #{item.chalf})
