mapper.select:
SELECT CRCLM_YEAR,CRCLM_HALF, CodeFind('0018',CRCLM_HALF) AS HALF,CRCLM_CD, CodeFind('0002',CRCLM_CD) AS CRCLM_NM, SBJCT_NO, SBJCT_NM, SBJCT_EXPLN, EDU_HR, SBJCT_PLAN_YN, ROOM_NO, ESNTL_YN FROM ESTBL_SBJCT
WHERE CRCLM_CD = #{crc}

	AND CRCLM_YEAR = #{year}


	AND CRCLM_HALF = #{hlf}

SELECT CRCLM_YEAR FROM CRCLM_INFO
WHERE CRCLM_CD = #{crc}
GROUP BY CRCLM_YEAR

SELECT * FROM SBJCT_INSTR NATURAL JOIN INSTR_INFO
WHERE
	CRCLM_CD = #{crc}
	AND CRCLM_YEAR = #{year}
	AND CRCLM_HALF = #{hlf}
	AND SBJCT_NO = #{sbjno}
