mapper.select:
SELECT * FROM SBJCT_PLAN
WHERE CodeFind('0002',CRCLM_CD) = #{crc} 
	AND CRCLM_YEAR = #{year} 
	AND CodeFind('0018',CRCLM_HALF) = #{hlf} 
	AND SBJCT_NO = #{sbj}

SELECT * FROM SBJCT_PLAN_DTL
WHERE CodeFind('0002',CRCLM_CD) = #{crc} 
	AND CRCLM_YEAR = #{year} 
	AND CodeFind('0018',CRCLM_HALF) = #{hlf} 
	AND SBJCT_NO = #{sbj}

mapper.update:
UPDATE SBJCT_PLAN
SET
SBJCT_TRGT = #{trgt}
,SBJCT_CN = #{cn}
,CRS_BOOK = #{book}
,SBJCT_MTHD_CD = #{method}
WHERE CodeFind('0002',CRCLM_CD) = #{crc} 
	AND CRCLM_YEAR = #{year} 
	AND CodeFind('0018',CRCLM_HALF) = #{hlf} 
	AND SBJCT_NO = #{sbj}

UPDATE ESTBL_SBJCT
SET
SBJCT_PLAN_YN = 'Y'
WHERE CodeFind('0002',CRCLM_CD) = #{crc} 
	AND CRCLM_YEAR = #{year} 
	AND CodeFind('0018',CRCLM_HALF) = #{hlf} 
	AND SBJCT_NO = #{sbj}
