mapper.select:
SELECT crclm.CRCLM_CD AS crclm_cd, crclm.CRCLM_YEAR AS crclm_year, crclm.CRCLM_HALF AS crclm_half, crclm.CRCLM_NM AS crclm_name, crclm.EDU_NOPE AS person_no , LPAD((SELECT COUNT(*) FROM APPL_INFO WHERE APPL_INFO.CRCLM_CD = crclm.CRCLM_CD AND APPL_INFO.CRCLM_YEAR = #{year } AND APPL_INFO.CRCLM_HALF = #{half }), 2, '0') AS applCount FROM CRCLM_INFO crclm LEFT JOIN APPL_INFO appl ON crclm.CRCLM_CD = appl.CRCLM_CD WHERE crclm.CRCLM_YEAR = #{year } AND crclm.CRCLM_HALF = #{half }
AND crclm.CRCLM_NM LIKE CONCAT('%',#{crclmName },'%')
GROUP BY crclm.CRCLM_CD

SELECT KORN_FLNM as stu_name, TELNO as stu_tel, STDNT_NO as stu_no, CRCLM_CD AS crclm_cd
		, CASE WHEN (SELECT USER_ID FROM USER_INFO WHERE STDNT_NO = USER_INFO.USER_ID) != ' ' THEN 'Y' ELSE 'N' END AS ID_YN
FROM APPL_INFO
WHERE CRCLM_YEAR = #{year } and CRCLM_HALF = #{half }

		AND CRCLM_CD = #{crclm_cd }


		AND KORN_FLNM = #{stdntName }

update.foreach:
UPDATE APPL_INFO
SET STDNT_NO = #{setNoList.stdnt_no }
WHERE USER_NO = #{setNoList.user_no }
