mapper.select:
SELECT * FROM applinfoview


AND CRCLM_CD = #{department }


AND REG_CD = #{status }


AND (CRCLM_YEAR LIKE CONCAT('%',#{year },'%'))


AND CRCLM_HALF = #{turn }


AND (STDNT_NO LIKE CONCAT('%',#{nameAndNum },'%') or KORN_FLNM LIKE CONCAT('%',#{nameAndNum },'%'))


AND GENDER_CD = #{gender }
