mapper.select:

SELECT * FROM surveylist
WHERE 1=1

		AND CRCLM_YEAR = #{s_CRCLM_YEAR}


		AND CRCLM_HALF = #{s_CRCLM_HALF}


		AND SBJCT_NM LIKE CONCAT('%',#{s_SBJCT_NM},'%')		


		AND CRCLM_CD = #{s_CRCLM_CD}

mapper.update:
UPDATE APPL_DGSTFN_ITEM SET DGSTFN_CN = CASE
WHEN CRCLM_CD = #{item.CRCLM_CD} AND CRCLM_YEAR = #{item.CRCLM_YEAR} AND CRCLM_HALF = #{item.CRCLM_HALF} AND SBJCT_NO = #{item.SBJCT_NO} AND DGSTFN_NO = #{item.DGSTFN_NO} THEN #{item.DGSTFN_CN}
ELSE DGSTFN_CN END, QITEM_CD = CASE
WHEN CRCLM_CD = #{item.CRCLM_CD} AND CRCLM_YEAR = #{item.CRCLM_YEAR} AND CRCLM_HALF = #{item.CRCLM_HALF} AND SBJCT_NO = #{item.SBJCT_NO} AND DGSTFN_NO = #{item.DGSTFN_NO} THEN #{item.QITEM_CD}
ELSE QITEM_CD END, SORT_SN = CASE
WHEN CRCLM_CD = #{item.CRCLM_CD} AND CRCLM_YEAR = #{item.CRCLM_YEAR} AND CRCLM_HALF = #{item.CRCLM_HALF} AND SBJCT_NO = #{item.SBJCT_NO} AND DGSTFN_NO = #{item.DGSTFN_NO} THEN #{item.DGSTFN_NO}
ELSE SORT_SN END
CRCLM_CD = #{item.CRCLM_CD} AND CRCLM_YEAR = #{item.CRCLM_YEAR} AND CRCLM_HALF = #{item.CRCLM_HALF} AND SBJCT_NO = #{item.SBJCT_NO} AND DGSTFN_NO = #{item.DGSTFN_NO}
